mapper.select:
SELECT

FROM
debug_detail
WHERE
1 = 1

    And platform_id=#{platformId,jdbcType=VARCHAR}


    And domain_id=#{domainId,jdbcType=VARCHAR}


    And app_name=#{appName,jdbcType=VARCHAR}


    And alias=#{alias,jdbcType=VARCHAR}

mapper.delete:
delete
from debug_detail
where 1=1

    And
    platform_id=#{platformId,jdbcType=VARCHAR}


    And
    domain_id=#{domainId,jdbcType=VARCHAR}


    And
    app_name=#{appName,jdbcType=VARCHAR}


    And
    alias=#{alias,jdbcType=VARCHAR}

mapper.update:
update debug_detail set
    detail = #{detail,typeHandler=com.channelsoft.ccod.support.cmdb.dao.typeHandler.AppOperationInfoHandler},
    update_time = #{updateTime,jdbcType=VARCHAR},
    debugging = #{debugging,jdbcType=BOOLEAN},
    try_count = #{tryCount,jdbcType=INTEGER}
where id = #{id,jdbcType=VARCHAR}
