mapper.select:
SELECT asb.assemble_id AS assemble_id, asb.platform_id AS platform_id, asb.domain_id AS domain_id, asb.tag AS tag, asb.status AS status FROM assemble asb WHERE 1 = 1
And asb.platform_id=#{platformId,jdbcType=VARCHAR}
And asb.domain_id=#{domainId,jdbcType=VARCHAR}

mapper.delete:
delete
from assemble
where 1=1

    And
    platform_id=#{platformId,jdbcType=VARCHAR}


    And
    domain_id=#{domainId,jdbcType=VARCHAR}


    And
    assemble_id=#{assembleId,jdbcType=INTEGER}

mapper.update:
update assemble set
    tag = #{tag,jdbcType=VARCHAR},
    status = #{status,jdbcType=VARCHAR}
where assemble_id = #{assembleId,jdbcType=VARCHAR}
