mapper.select:
select

from build_detail bd
WHERE
1 = 1

    And bd.job_name=#{jobName,jdbcType=VARCHAR}


    And bd.app_name=#{appName,jdbcType=VARCHAR}


    And bd.version=#{version,jdbcType=VARCHAR}


    And bd.start_time >= #{startTime,jdbcType=TIMESTAMP}


    And bd.end_time <= #{endTime,jdbcType=TIMESTAMP}

order by bd.end_time desc
