mapper.select:
select

from ccod_three_part_app
where 1=1

    And ccod_version=#{ccodVersion,jdbcType=VARCHAR}


    And tag=#{tag,jdbcType=VARCHAR}


    And app_name=#{appName,jdbcType=VARCHAR}

mapper.delete:
delete
from ccod_three_part_app
where 1=1
And ccod_version=#{ccodVersion,jdbcType=VARCHAR}
And tag=#{tag,jdbcType=VARCHAR}

    And app_name=#{appName,jdbcType=VARCHAR}


    And alias=#{alias,jdbcType=VARCHAR}
