mapper.select:
select

from domain dm
where dm.platform_id=#{platformId,jdbcType=VARCHAR} AND dm.domain_id=#{domainId,jdbcType=VARCHAR}

select

from domain dm
where dm.platform_id=#{platformId,jdbcType=VARCHAR} AND dm.domain_name=#{domainName,jdbcType=VARCHAR}

select

from domain dm
where 1=1

    And dm.platform_id=#{platformId,jdbcType=VARCHAR}


    And dm.status=#{status,jdbcType=INTEGER}

mapper.delete:
delete from domain where 1=1
And domain_id=#{domainId,jdbcType=VARCHAR}
And platform_id=#{platformId,jdbcType=VARCHAR}
