mapper.select:
select

from domain_public_config dpc
where 1=1

    And dpc.platform_id=#{platformId,jdbcType=VARCHAR}


    And dpc.domain_id=#{domainId,jdbcType=INTEGER}

mapper.delete:
delete
from domain_public_config
where 1=1

    And dpc.platform_id=#{platformId,jdbcType=VARCHAR}


    And dpc.domain_id=#{domainId,jdbcType=INTEGER}
