mapper.select:
select

from k8s_operation ko
WHERE
1 = 1

    And ko.job_id=#{jobId,jdbcType=VARCHAR}


    And ko.platform_id=#{platformId,jdbcType=VARCHAR}


    And ko.domain_id=#{domainId,jdbcType=VARCHAR}

mapper.delete:
delete
from k8s_operation
where
1 = 1

    And ko.job_id=#{jobId,jdbcType=VARCHAR}


    And ko.platform_id=#{platformId,jdbcType=VARCHAR}


    And ko.domain_id=#{domainId,jdbcType=VARCHAR}
