mapper.delete:
delete from platform_app_bk_module where 1=1
And platform_app_id=#{platformAppId,jdbcType=INTEGER}
And bk_biz_id=#{bkBizId,jdbcType=INTEGER}

mapper.select:
select

where 1=1

    And pa.platform_id=#{platformId,jdbcType=VARCHAR}


    And pa.domain_id=#{domainId,jdbcType=VARCHAR}


    And pakm.bk_biz_id=#{bkBizId,jdbcType=INTEGER}


    And pakm.bk_set_id=#{bkSetId,jdbcType=INTEGER}


    And pakm.bk_module_id=#{bkModuleId,jdbcType=INTEGER}


    And pakm.bk_host_id=#{bkHostId,jdbcType=INTEGER}
