mapper.select:
SELECT pa.platform_app_id, pa.app_id, pa.platform_id, pa.domain_id, pa.server_id, pa.runner_id, pa.base_path, pa.deploy_time FROM platform_app pa INNER JOIN app a ON pa.app_id = a.app_id INNER JOIN platform pf ON pa.platform_id = pf.platform_id INNER JOIN domain dm ON pa.domain_id = dm.domain_id INNER JOIN `server` svr ON pa.server_id = svr.server_id INNER JOIN server_user sur ON pa.runner_id = sur.user_id WHERE 1 = 1
And pf.platform_id=#{platformId,jdbcType=VARCHAR}
And dm.domain_id=#{domainId,jdbcType=VARCHAR}
And svr.host_ip=#{hostIp,jdbcType=VARCHAR}
And svr.host_name=#{hostname,jdbcType=VARCHAR}
And a.app_name=#{appName,jdbcType=VARCHAR}
And a.app_alias=#{appAlias,jdbcType=VARCHAR}
And a.version=#{version,jdbcType=VARCHAR}

mapper.delete:
delete
from platform_app
where 1=1

    And
    platform_app_id=#{platformAppId,jdbcType=INTEGER}


    And
    platform_id=#{platformId,jdbcType=VARCHAR}


    And
    domain_id=#{domainId,jdbcType=VARCHAR}


    And
    server_id=#{serverId,jdbcType=INTEGER}

mapper.update:
update platform_app set
    app_id = #{appId,jdbcType=VARCHAR}
where platform_app_id = #{platformAppId,jdbcType=VARCHAR}
