mapper.select:
SELECT pa.platform_app_id AS platform_app_id, pa.app_id AS app_id, pa.app_alias AS app_alias, pa.original_alias AS original_alias, pa.platform_id AS platform_id, pa.domain_id AS domain_id, pa.assemble_id AS assemble_id, pa.host_ip AS host_ip, pa.port AS port, pa.app_runner AS app_runner, pa.base_path AS base_path, pa.deploy_time AS deploy_time, pa.deploy_path AS deploy_path, pa.start_cmd AS start_cmd FROM platform_app pa INNER JOIN app a ON pa.app_id = a.app_id WHERE 1 = 1
And pa.platform_id=#{platformId,jdbcType=VARCHAR}
And pa.domain_id=#{domainId,jdbcType=VARCHAR}
And pa.host_ip=#{hostIp,jdbcType=VARCHAR}
And a.app_name=#{appName,jdbcType=VARCHAR}
And a.app_alias=#{appAlias,jdbcType=VARCHAR}
And a.version=#{version,jdbcType=VARCHAR}

mapper.delete:
delete
from platform_app
where 1=1

    And
    platform_app_id=#{platformAppId,jdbcType=INTEGER}


    And
    platform_id=#{platformId,jdbcType=VARCHAR}


    And
    domain_id=#{domainId,jdbcType=VARCHAR}

mapper.update:
update platform_app set
    deploy_path = #{deployPath,jdbcType=VARCHAR},
    start_cmd = #{startCmd,jdbcType=VARCHAR}
where platform_app_id = #{platformAppId,jdbcType=VARCHAR}
