mapper.delete:
delete from platform_three_part_app where 1=1
And platform_id=#{platformId,jdbcType=VARCHAR}
And three_part_app_id=#{threePartAppId,jdbcType=INTEGER}

mapper.update:
update platform_three_part_app set
    host_ip = #{hostIp,jdbcType=VARCHAR},
    port = #{port,jdbcType=VARCHAR},
    status = #{status,jdbcType=VARCHAR}
where three_part_app_id = #{threePartAppId,jdbcType=VARCHAR}
