mapper.select:
select
from platform_update_record pur where 1=1
And platform_id=#{platformId,jdbcType=VARCHAR}
And is_last=#{isLastUpdate,jdbcType=BOOLEAN}

select

from platform_update_record pur
where pur.job_id=#{jobId,jdbcType=VARCHAR}

mapper.delete:
delete from platform_update_record where 1=1
And platform_id=#{platformId,jdbcType=VARCHAR}
And job_id=#{jobId,jdbcType=VARCHAR}

mapper.update:
update platform_update_record set
    is_last = #{isLast,jdbcType=BOOLEAN}
where record_id = #{recordId,jdbcType=INTEGER}
