mapper.select:
select

from domain dm
where dm.domain_id=#{domainId,jdbcType=VARCHAR}

select

from domain dm
where 1=1

    And dm.platform_id=#{platformId,jdbcType=INTEGER}


    And dm.status=#{status,jdbcType=INTEGER}

mapper.delete:
delete from domain where 1=1
And domain_id=#{domainId,jdbcType=VARCHAR}
And platform_id=#{platformId,jdbcType=VARCHAR}

mapper.update:
update domain set
    domain_name = #{domainName,jdbcType=VARCHAR},
    status = #{status,jdbcType=INTEGER},
    comment = #{comment,jdbcType=VARCHAR},
    update_time = #{updateTime,jdbcType=TIMESTAMP}
where domain_id = #{domainId,jdbcType=VARCHAR}
